

import * as THREE from 'three';
function renderClickCapture({ event, renderer, OutlinePass, camera, scene }) {
    let width = renderer.renderer.domElement.width;
    let height = renderer.renderer.domElement.height;
    // .offsetY、.offsetX以canvas画布左上角为坐标原点,单位px
    const px = event.offsetX;
    const py = event.offsetY;
    //屏幕坐标px、py转WebGL标准设备坐标x、y
    //width、height表示canvas画布宽高度
    const x = (px / width) * 2 - 1;
    const y = -(py / height) * 2 + 1;
    //创建一个射线投射器`Raycaster`
    const raycaster = new THREE.Raycaster();
    //.setFromCamera()计算射线投射器`Raycaster`的射线属性.ray
    // 形象点说就是在点击位置创建一条射线，射线穿过的模型代表选中
    raycaster.setFromCamera(new THREE.Vector2(x, y), camera);
    //.intersectObjects([mesh1, mesh2, mesh3])对参数中的网格模型对象进行射线交叉计算
    // 未选中对象返回空数组[],选中一个对象，数组1个元素，选中两个对象，数组两个元素
    const intersects = raycaster.intersectObjects(scene.children).filter(item => item.object.ancestors);
    console.log("射线器返回的对象", intersects);
    // intersects.length大于0说明，说明选中了模型
    if (intersects.length > 0) {
        // outlinePass.selectedObjects = 
        // 选中模型的第一个模型，设置为红色
        OutlinePass.selectedObjects = [ intersects[ 0 ]?.object?.ancestors ];
    }
}
export { renderClickCapture };